import React, { useRef, useEffect, useState } from "react";
import * as THREE from "three";
import { OrbitControls } from "three/examples/jsm/controls/OrbitControls";

const ThreeScene = () => {
  const mountRef = useRef(null); // 引用 DOM 节点
  const [objects, setObjects] = useState([]); // 存储场景中的物体

  useEffect(() => {
    // 创建场景、相机、渲染器
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.set(10, 10, 20);

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.shadowMap.enabled = true;
    mountRef.current.appendChild(renderer.domElement);

    // 添加轨道控制器
    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true; // 惯性效果
    controls.dampingFactor = 0.05;
    controls.screenSpacePanning = false;

    // 添加光源
    const ambientLight = new THREE.AmbientLight(0xffffff, 0.5); // 环境光
    scene.add(ambientLight);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8); // 定向光
    directionalLight.position.set(10, 20, 10);
    directionalLight.castShadow = true;
    scene.add(directionalLight);

    // 添加地面
    const planeGeometry = new THREE.PlaneGeometry(100, 100);
    const planeMaterial = new THREE.MeshStandardMaterial({ color: 0x808080 });
    const plane = new THREE.Mesh(planeGeometry, planeMaterial);
    plane.rotation.x = -Math.PI / 2;
    plane.receiveShadow = true;
    scene.add(plane);

    // 动态添加和删除物体的逻辑
    const addObject = () => {
      const boxGeometry = new THREE.BoxGeometry(2, 2, 2);
      const boxMaterial = new THREE.MeshStandardMaterial({ color: Math.random() * 0xffffff });
      const box = new THREE.Mesh(boxGeometry, boxMaterial);
      box.position.set(
        Math.random() * 10 - 5,
        1,
        Math.random() * 10 - 5 // 随机放置在地面上
      );
      box.castShadow = true;
      scene.add(box);

      setObjects((prev) => [...prev, box]); // 更新状态
    };

    const removeObject = () => {
      if (objects.length > 0) {
        const objectToRemove = objects.pop();
        scene.remove(objectToRemove);
        objectToRemove.geometry.dispose();
        objectToRemove.material.dispose();

        setObjects([...objects]); // 更新状态
      }
    };

    // 渲染循环
    const animate = () => {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    };
    animate();

    // 清理资源
    return () => {
      renderer.dispose();
      mountRef.current.removeChild(renderer.domElement);
    };
  }, [objects]); // 依赖 objects 更新

  return (
    <div>
      <div ref={mountRef} style={{ width: "100%", height: "100vh" }} />
      <div style={{ position: "absolute", top: 10, left: 10 }}>
        <button onClick={() => setObjects((prev) => [...prev, "ADD"])}>添加物体</button>
        <button onClick={() => setObjects((prev) => [...prev, "REMOVE"])}>删除物体</button>
      </div>
    </div>
  );
};

export default ThreeScene;
